import {useState} from 'react';
const Home = () => {

    const [name,setName]=useState('luigi');
    const [age,setAge]=useState(20);

    const handleClick=()=>{
        setName('Tala');
        setAge(50);
     }

    return (
        <div className="Home">
            <h2>HomePage</h2>
            <p>{name} is {age} years old</p>
            <button onClick={handleClick}> click me</button>
        </div>
    );
}

export default Home;
