import {useEffect, useState} from 'react';
import BlogList from './BlogList';

const Home = () => {
    const [blogs,setBlogs]=useState([
        {title:'My new website', body:'lorem ipsum doratri cobaotp nati japeto.....',author:'tanyu',id:1},
        {title:'Welcome party!', body:'lorem ipsum doratri cobaotp nati japeto.....',author:'yoshi',id:2},
        {title:'Web dev tool tips', body:'lorem ipsum doratri cobaotp nati japeto.....',author:'tanyu',id:3},
    ]);

    const [name,setName]=useState('mario');

    const handleDelete=(id)=>{
        const NewBlog=blogs.filter((blog)=>blog.id!=id);
        setBlogs(NewBlog);
    };

    useEffect(()=>{
        console.log('useEffect has been executed');
        console.log(name);
    },[name]);

    return (
        <div className="Home">
          <BlogList blogs={blogs} title='All Blogs On This Page!' handleDelete ={handleDelete}/>
          <button onClick={()=>setName('brandon')}>Change Name</button>
          <h2>{name}</h2>
          {/* <BlogList blogs={blogs.filter((blog)=>blog.author==='tanyu')} title="Tanyu's blog"/> */}
        </div>
    );
}

export default Home;
