const useFetch=(url)=>{

    const [data,setData]=useState(null);

    const[isPending,setIsPending]=useState(true);

    const[error,setError]=useState(null);

    useEffect(()=>{
        setTimeout(()=>{
            fetch(url)
            .then(res=>{
                if(!res.ok){
                    throw Error("A fatal error occured!");
                };
               return  res.json();
            })
            .then(data=>{
                 setData(data);   
                 setIsPending(false);
            })
            .catch((e)=>{
                setError(e.message);
                setIsPending(false);
            });
        },1000)
    },[url]);

    return {data,isPending,error}
};

export default useFetch;
